    static String[] inputs = input.split(" ");

    public static void main(String... args) {
        for (int j = 0; j < inputs.length; j++) {
            String input = inputs[j];

            for (int i = 0; i < input.length(); i++) {
                Coordinate coordinate = new Coordinate(i, j);
                coordinate.xvalue = i;
                coordinate.yvalue = j;
                Piece piece = null;
                char c = input.charAt(i);
                if (c == '-') {
                    piece = new Piece();
                    piece.connectionCoordinate1 = new Coordinate(i - 1, j);
                    piece.connectionCoordinate2 = new Coordinate(i + 1, j);
                } else if (c == '|') {
                    piece = new Piece();
                    piece.connectionCoordinate1 = new Coordinate(i, j - 1);
                    piece.connectionCoordinate2 = new Coordinate(i, j + 1);
                } else if (c == '+') {
                    piece = new Piece();
                    piece.connectsAllCoordanates = true;
                } else if (c == '<') {
                    piece = new Piece();
                    piece.connectionCoordinate1 = new Coordinate(i, j - 1);
                    piece.connectionCoordinate2 = new Coordinate(i, j + 1);
                } else if (c == '|') {
                    piece = new Piece();
                    piece.connectionCoordinate1 = new Coordinate(i, j - 1);
                    piece.connectionCoordinate2 = new Coordinate(i, j + 1);
                } else if (c == '|') {
                    piece = new Piece();
                    piece.connectionCoordinate1 = new Coordinate(i, j - 1);
                    piece.connectionCoordinate2 = new Coordinate(i, j + 1);
                } else if (c == '|') {
                    piece = new Piece();
                    piece.connectionCoordinate1 = new Coordinate(i, j - 1);
                    piece.connectionCoordinate2 = new Coordinate(i, j + 1);
                } else if (c == '\\') {
                    piece = new Piece();
                    piece.connectionCoordinate1 = new Coordinate(i, j - 1);
                    piece.connectionCoordinate2 = new Coordinate(i, j + 1);
                } else if (c == '/') {
                    piece = new Piece();
                    piece.connectionCoordinate1 = new Coordinate(i, j - 1);
                    piece.connectionCoordinate2 = new Coordinate(i, j + 1);
                }
            }
        }
    }

    private static class Coordinate {

        public Coordinate(int a, int b) {
            this.xvalue = a;
            this.yvalue = b;
        }

        public int xvalue;
        public int yvalue;
        public Piece piece;
    }

    private static class Piece {
        public Coordinate connectionCoordinate1;
        public Coordinate connectionCoordinate2;
        public boolean connectsAllCoordanates;
        public boolean containsKart;
    }
